import React, { Component } from 'react';
import Header from './Header';
import Player from './Player';
import AddPlayerForm from './AddPlayerForm';


class App extends Component {
  state = {
    players: [
      {
        name: "Bobby",
        score: 0,
        id: 1
      },
      {
        name: "Treasure",
        score: 0,
        id: 2
      },
      {
        name: "Ashley",
        score: 0,
        id: 3
      },
      {
        name: "James",
        score: 0,
        id: 4
      }
    ]
  };

    //player id counter
    prevPlayerId = 4;

    handleScoreChange = (index, delta) => {
      this.setState( prevState => ({
        score: prevState.players[index].score += delta
      }));
      console.log(index, delta); /*used to show index tracking on click handler when index was added */
    }

    handleAddPlayer = (name) => {
      this.setState( prevState => {
        return {
          players: [
            ...prevState.players,
            {
              name: name,
              score: 0,
              id: this.prevPlayerId += 1
            }
          ]
        };
      });
    }

   handleRemovePlayer = (id) => {
    this.setState( prevState => {
      return {
        players: prevState.players.filter(p => p.id !== id)
      };
    });
  }

    getHighScore = () => { //part of the stretch goal/challenge
      const scores = this.state.players.map(p => p.score);
      const highScore = Math.max(...scores);
      if (highScore) {
        return highScore;
      }
      return null;
  }

  render() {

    const highScore = this.getHighScore();
    return (
      <div className="scoreboard">
        <Header 
          // title="Scoreboard" //we can delete this because we set a default props in Header.js
          players={this.state.players}
        />

        {/* Players list */}
        {this.state.players.map( (player, index) =>
          <Player 
            name={player.name}
            score={player.score}
            id={player.id}
            index={index}
            key={player.id.toString()}
            changeScore={this.handleScoreChange} 
            removePlayer={this.handleRemovePlayer}
            isHighScore ={highScore === player.score} //is a players score prop equal to the high score?           
          />
        )}

        <AddPlayerForm addPlayer={this.handleAddPlayer}/>
      </div>
    );
  }
}

export default App;
