import React, { Component } from 'react';

class Stopwatch extends Component {

  state = {
    isRunning: false
  };



  render() {
    return (
      <div className="stopwatch">
        <h2>Stopwatch</h2>
        <span className="stopwatch-time">0</span>
        <button>{ this.state.isRunning ? "stop" : "start" }</button>
        <button>Reset</button>
      </div>
    );
  }
}

export default Stopwatch;
